		glaciers

			(SELECT way,"natural" FROM &prefix;_polygon) AS glaciers 
			&datasource-settings;




		residential-fill

			(SELECT way,amenity,landuse FROM &prefix;_polygon WHERE landuse IN ('residential','industrial') OR amenity IN ('university','school','hospital')) AS landuse 
			&datasource-settings;




		waterway-lines

			(SELECT way,"CEMT",intermittent,motorboat,tunnel,waterway FROM &prefix;_line) AS water 
			&datasource-settings;




		waterway-polygons-casing
		waterway-polygons-fill
		water-areas

			(SELECT way,"CEMT",motorboat,"natural",landuse,leisure,waterway,way_area FROM &prefix;_polygon) AS water 
			&datasource-settings;




		water-weir
		waterway-arrows

			(SELECT way,waterway,intermittent FROM &prefix;_line) AS water 
			&datasource-settings;




		tunnels-casing
		tunnels-fill
		tunnels-middle

			(SELECT way,highway,tracktype FROM &prefix;_line WHERE (tunnel = 'yes') AND highway IS NOT NULL) AS roads 
			&datasource-settings;




		cliffs-draw

			(SELECT way,"natural","man_made",highway FROM &prefix;_line) AS cliff 
			&datasource-settings;




		cliffs-draw

			(SELECT way,area,"natural","man_made",NULL AS highway FROM &prefix;_polygon WHERE (area != 'yes')) AS cliff 
			&datasource-settings;




		buildings-fill

			(SELECT way,building,leisure,railway,amenity,aeroway FROM &prefix;_polygon WHERE (building IS NOT NULL AND building != 'no') OR (aeroway = 'terminal')) AS buildings 
			&datasource-settings;




		military-fill

			(SELECT way,landuse FROM &prefix;_polygon WHERE (landuse = 'military')) AS landuse 
			&datasource-settings;






	&layer-contours;



		borders-draw

			(SELECT way,boundary,admin_level FROM &prefix;_line WHERE boundary IN ('administrative')) AS borders 
			&datasource-settings;




		railways-embankment
		railways-draw

			(SELECT way,railway,tunnel,embankment,service FROM &prefix;_line WHERE railway IS NOT NULL) AS railways 
			&datasource-settings;




		airports-casing
		airports-fill

			(SELECT way,aeroway FROM &prefix;_line WHERE aeroway IS NOT NULL) AS airports 
			&datasource-settings;




		roads-lowzoom-casing
		roads-lowzoom-fill

			(SELECT way,highway FROM &prefix;_roads) AS roadslowzoom 
			&datasource-settings;





	roads-casing

			(SELECT way,highway,service,surface,tracktype,embankment FROM &prefix;_line WHERE (!scale_denominator! > 150000) OR ((bridge IS NULL OR bridge != 'yes') AND (tunnel IS NULL OR tunnel != 'yes')) AND highway IS NOT NULL) AS roads 
			&datasource-settings;




	roads-areas

			(SELECT way,area,highway FROM &prefix;_polygon WHERE highway IS NOT NULL AND area IS NOT NULL) AS areas 
			&datasource-settings;




		roads-fill
		roads-12_13
		roads-middle
		trams-draw
		roads-text_z16

			(SELECT way,highway,service,tracktype,name,railway FROM &prefix;_line WHERE (!scale_denominator! > 150000) OR ((bridge IS NULL OR bridge != 'yes') AND (tunnel IS NULL OR tunnel != 'yes')) AND ((highway IS NOT NULL) AND (highway != 'construction')) OR (railway='tram')) AS roads 
			&datasource-settings;




		ferry_routes-draw

			(SELECT way,route FROM &prefix;_line) AS route 
			&datasource-settings;






		railways-bridge

			(SELECT way,railway FROM &prefix;_line WHERE (bridge = 'yes') AND (railway IS NOT NULL)) AS railways 
			&datasource-settings;





		bridges-casing
		bridges-fill
		bridges-middle
		trams-draw

			(SELECT way,highway,surface,tracktype,layer,railway FROM &prefix;_line WHERE (bridge = 'yes') AND (highway IS NOT NULL OR railway = 'tram') AND (layer IS NULL OR layer <= '1')) AS roads 
			&datasource-settings;




		bridges-casing
		bridges-fill
		bridges-middle
		trams-draw

			(SELECT way,highway,surface,tracktype,layer,railway FROM &prefix;_line WHERE (bridge = 'yes') AND (highway IS NOT NULL OR railway = 'tram') AND (layer = '2')) AS roads 
			&datasource-settings;




		bridges-casing
		bridges-fill
		bridges-middle
		trams-draw

			(SELECT way,highway,surface,tracktype,layer,railway FROM &prefix;_line WHERE (bridge = 'yes') AND (highway IS NOT NULL OR railway = 'tram') AND (layer > '2')) AS roads 
			&datasource-settings;





		aerialways-draw

			(SELECT way,aerialway FROM &prefix;_line WHERE aerialway IS NOT NULL) AS aerialway 
			&datasource-settings;




		powerlines-draw

			(SELECT way,power,voltage FROM &prefix;_line) AS powerlines 
			&datasource-settings;



		powertowers-draw

			(SELECT way,power FROM &prefix;_point WHERE power IN ('tower','pole')) AS powertowers 
			&datasource-settings;




		fences-draw

			(SELECT way,barrier,fenced FROM &prefix;_polygon WHERE (ST_Perimeter(way)::integer > 400)) AS fences 
			&datasource-settings;



		fences-draw

			(SELECT way,barrier,fenced FROM &prefix;_line WHERE (ST_Length(way)::integer > 300)) AS fences 
			&datasource-settings;




		text-natural-lines

			(SELECT way,name,"natural" FROM &prefix;_line) AS names
			&datasource-settings;




		text-capitals
		text-cities
		text-towns
		text-villages

			(SELECT way,admin_level,name,capital,place,population::INTEGER FROM &prefix;_point WHERE place IN ('city','town','village','suburb','hamlet','isolated_dwelling','locality') AND (population IS NULL OR population SIMILAR TO '[[:digit:]]+') order by (CASE WHEN place='city' THEN 0 WHEN place='town' THEN 1 WHEN place='village' THEN 2 ELSE 3 END) asc, (CASE WHEN population IS NUll THEN 0 WHEN population similar to '[[:digit:]]+' THEN population::integer ELSE 0 END) desc
		  ) AS names
			&datasource-settings;




		text-natural

			(SELECT way,name,"natural",way_area FROM &prefix;_polygon) AS names
			&datasource-settings;




		text-peaks

			(SELECT way,name,"natural",ele FROM &prefix;_point) AS names
			&datasource-settings;




		symbols-churches-castles
		symbols-draw
		symbols-pv

			(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,religion,denomination,denotation,NULL AS fee,"man_made","tower:type","communication:radio","communication:television",building,"natural",name,ruins,NULL AS "summit:cross",tourism,"power","generator:source","site_type",sport,type,disused,abandoned,railway,way_area FROM &prefix;_polygon) AS symbols 
			&datasource-settings;




		symbols-churches-castles
		symbols-draw
		symbols-viewpoints

			(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,religion,denomination,denotation,NULL AS fee,"man_made","tower:type","communication:radio","communication:television",building,"natural",name,ruins,"summit:cross",tourism,"power","generator:source","site_type",sport,type,disused,abandoned,railway FROM &prefix;_point) AS symbols 
			&datasource-settings;




		text-roads

			(SELECT way,highway,ref FROM &prefix;_line WHERE (ST_Length(way)::integer > 1000) AND ref IS NOT NULL) AS roads 
			&datasource-settings;




		text-waterways

			(SELECT way,name,waterway FROM &prefix;_line) AS names
			&datasource-settings;
